mapper.select:
select
bu_clothes_order_log(client_id, client_phone, clothes_id, order_price, order_discount, actual_price, remark,
paper_num, order_num, status, del_flag, create_by, create_time, update_by, update_time)

from bu_clothes_order_log


        and id = #{id}


        and client_id = #{clientId}


        and client_phone = #{clientPhone}


        and clothes_id = #{clothesId}


        and order_price = #{orderPrice}


        and order_discount = #{orderDiscount}


        and actual_price = #{actualPrice}


        and remark = #{remark}


        and paper_num = #{paperNum}


        and order_num = #{orderNum}


        and status = #{status}


        and del_flag = #{delFlag}


        and create_by = #{createBy}


        and create_time = #{createTime}


        and update_by = #{updateBy}


        and update_time = #{updateTime}


limit #{pageable.offset}, #{pageable.pageSize}

select count(1)
from bu_clothes_order_log


        and id = #{id}


        and client_id = #{clientId}


        and client_phone = #{clientPhone}


        and clothes_id = #{clothesId}


        and order_price = #{orderPrice}


        and order_discount = #{orderDiscount}


        and actual_price = #{actualPrice}


        and remark = #{remark}


        and paper_num = #{paperNum}


        and order_num = #{orderNum}


        and status = #{status}


        and del_flag = #{delFlag}


        and create_by = #{createBy}


        and create_time = #{createTime}


        and update_by = #{updateBy}


        and update_time = #{updateTime}
